$.ajax({
    url: `${Inputs.backendUrl}auth/roles`, 
    method: "GET",
    
   success: function(data) {
        fetchSuccess(data);
    },
   
     error: function(xhr, status, error) {
        // Try to parse error as JSON, fallback to string
        let errorDetail = "";
        try {
            errorDetail = JSON.parse(xhr.responseText).detail;
        } catch (e) {
            errorDetail = xhr.responseText || error;
        }
        fetchError({ 
            error: { 
                error: errorDetail, 
                message: "Registration failed!" 
            } 
        });
    }
});

function fetchSuccess(data){
    Outputs.excuteSuccess();
    const result = data.map((category, index) => ({
        Label: `${category.name}`,
        Value: `${category.id}`
    }));
    Outputs.data = result;
   
}

function fetchError(data){
    Outputs.excuteError();
    Outputs.dataErr = [];
    
}

