    $.ajax({
        url: `${Inputs.backendUrl}documents/${Inputs.id}/download`,
        method: "GET",

        xhrFields: {
            responseType: 'blob' // Important for downloading files
        },
        success: function (data, status, xhr) {
            // Create blob URL
            const blob = new Blob([data], { type: xhr.getResponseHeader('Content-Type') });
            const url = window.URL.createObjectURL(blob);

            // Extract filename from Content-Disposition
            let fileName = 'downloaded_file';
            const contentDisposition = xhr.getResponseHeader('Content-Disposition');
            if (contentDisposition && contentDisposition.includes('filename=')) {
                fileName = contentDisposition.split('filename=')[1].replace(/"/g, '');
            }

            // Create download link
            const link = document.createElement('a');
            link.href = url;
            link.download = fileName;
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);

            // Clean up
            window.URL.revokeObjectURL(url);
            downloadSuccess("download Successfully download")
            
        },
        error: function (xhr) {
            let errorDetail = "";
            try {
                errorDetail = JSON.parse(xhr.responseText).detail;
            } catch (e) {
                errorDetail = xhr.responseText || xhr.statusText;
            }
            downloadFailed()
            console.error("Error downloading document:", errorDetail);
            alert(`Failed to download document: ${errorDetail}`);
        }
    });

