const scriptUrl = "https://cdn.jsdelivr.net/npm/vanillajs-datepicker@1.2.0/dist/js/datepicker-full.min.js"
const cssUrl =  "https://cdn.jsdelivr.net/npm/vanillajs-datepicker@1.2.0/dist/css/datepicker.min.css"

function injectDatePicker(onload) {
    if (document.querySelectorAll('#--ndl-datepicker-datepicker-js--').length === 0) {
        window.__ndl_datepicker_onload = [onload]

        var script = document.createElement('script');
        script.id = "--ndl-datepicker-datepicker-js--"
        script.setAttribute('src', scriptUrl);
        script.onload = () => {
            window.__ndl_datepicker_onload.forEach(cb => cb())
            window.__ndl_datepicker_onload = []
        }
        document.head.appendChild(script)
    }
    else {
        if (window.__ndl_datepicker_onload.length > 0) window.__ndl_datepicker_onload.push(onload)
        else onload()
    }
}

function injectDatePickerStyles() {
    const datePickerStyles = `
        .ndl-datepicker-native-input {
            position: absolute;
            left: 0;
            top: 0;
            width: 100%;
            height: 100%;
            opacity: 0;
            cursor: pointer;
            box-sizing: border-box;
        }
        .ndl-datepicker-native-input::-webkit-calendar-picker-indicator {
            position: absolute;
            left: 0;
            top: 0;
            width: 100%;
            height: 100%;
            margin: 0;
            padding: 0;
            cursor: pointer;
        }

        .datepicker {
            display:none;
            font-family: Roboto, sans-serif;
            border: 1px solid ${Script.Inputs.Accent};
            border-radius: 4px;
        }

        .datepicker-picker {
            background-color: ${Script.Inputs.Background};
            padding: 24px;
        }

        .datepicker-header {
            padding-bottom: 14px;
        }

