// Function to add li elements to the dropdown menu
function addDropdownItems(menuId, items) {
const dropdownMenu = document.querySelector(`[data-testid="${menuId}"]`);
  if (dropdownMenu) {
    // Clear existing items (optional)
    dropdownMenu.innerHTML = '';

    items.forEach(item => {
      const li = document.createElement('li');
      const a = document.createElement('a');
      a.href = '#'; // or any other link
      a.textContent = item.text;
      a.onclick = function(event) {
        event.preventDefault();
        // Handle click event
        console.log(`Clicked on: ${item.text}`);
      };
      li.appendChild(a);
      dropdownMenu.appendChild(li);
    });
  }
}

// Example usage
const dropdownItems = [
  { text: 'Option 1' },
