//         last_name: Inputs.last_name,
//     }),
//     success: function(data) {
//         sendOutputs({ success: { data: data } });
//     },
//     error: function(xhr, status, error) {
//         sendOutputs({ error: { error: xhr.responseText || error } });
//     }
// });

$.ajax({
    url: `${Inputs.backendUrl}auth/register`, 
    method: "POST",
    contentType: "application/json",
    data: JSON.stringify({
        email: Inputs.email,
        password: Inputs.password,
        first_name: Inputs.first_name,
        last_name: Inputs.last_name,
    }),
    success: function(data) {
        // Send the data and a message
        sendOutputsSuccess({ 
            success: { 
                data: data, 
                message: "Registration successful!" // You can add any message here
            } 
        });
        window.location.href = "/login";
    },
   
     error: function(xhr, status, error) {
        // Try to parse error as JSON, fallback to string
        let errorDetail = "";
        try {
            errorDetail = JSON.parse(xhr.responseText).detail;
        } catch (e) {
            errorDetail = xhr.responseText || error;
        }
        sendOutputsError({ 
            error: { 
                error: errorDetail, 
                message: "Registration failed!" 
            } 
        });
    }
});

function sendOutputsSuccess(data){
    Outputs.excuteSuccess();
    Outputs.message = data.detail;
}

function sendOutputsError(data){
    Outputs.excuteError();
    Outputs.messageErro = data.error.error;
}

