$(document).ready(function () {
   $(".adconmain").hide();
  $(".adconmain").first().show(); 
  $(".sbarnavi .ndl-controls-button").on("click", function () {
    // Remove active state from siblings
    $(this).parent().siblings().removeClass("active");
    $(this).parent().addClass("active");

    // Hide all panels
    $(".adconmain").hide();

    // Get testid from clicked button
    const testId = $(this).attr("data-testid");
    if (!testId) return;

    // Build target ID with prefix "sh"
    const targetId = "sh" + testId;


    console.log("Target section:", targetId);

    // Find panel with matching testid and show it
    $(`[data-testid='${targetId}']`).show();
  });
});

