parameters.functionScript:
$.ajax({
   url: Inputs.url + "authentication/vendor-login",
   type: "POST",
   data: JSON.stringify({
      email: Inputs.email,
      password: Inputs.password
   }),
   dataType: "json", // expect JSON response
   contentType: "application/json; charset=utf-8",

   beforeSend: function () {
      $("[data-testid='loginButton']")
         .prop("disabled", true)
         .text("Logging in...");
   },

   success: function (response) {
      if(response.statusCode === 401)
      {
         Outputs.message = response.message ;
         Outputs.erroExcutr();
          setTimeout(function () {
         $("[data-testid='loginButton']")
            .prop("disabled", false)
            .text("Login");
      }, 2000);
      }
      if (response && response.data.session.token) {
         // Store token securely
         console.log(response)
         const user_id = response.data.user.id || 0;
         const token = response.data.session.token || 0;
         const session = response.data.session;
         const vendor_id = response.data.user.vendor.id || 0;
         localStorage.setItem("user_id", user_id);
         localStorage.setItem("token", token);
         localStorage.setItem("session", JSON.stringify(session));
         localStorage.setItem("vendor", vendor_id);


         Outputs.message = response.data.message ?? "Login successfull"

         setTimeout(function () {
            window.location.href = "/vendor";
         }, 2000);

      } else {
         alert("Unexpected response, please try again.");
         console.log("Response:", response);
      }
   },

   error: function (xhr, status, error) {
      let message = "Login Failed. Please try again.";

      if (xhr.status === 401) {
         message = "Invalid username or password.";
      } else if (xhr.status === 500) {
         message = "Server error. Please try later.";
      }

      alert(message);
      console.error("Error:", xhr.responseText || error);
   },

   complete: function () {
      setTimeout(function () {
         $("[data-testid='loginButton']")
            .prop("disabled", false)
            .text("Login");
      }, 5000);
   }
});

