parameters.functionScript:
const numPages = Math.ceil(Inputs.TotalCount / Inputs.PageSize);
const selectedPage = Inputs.SelectedPage;

// The number of direct pages that first in the control
const _width = Inputs.Width - 214; // Remove margin, buttons etc
if(isNaN(_width)) return;

const numPageSlots = Math.min(Math.floor(_width / 45),numPages);


if(numPages > 1) {
    Outputs.LastPageIndex = numPages-1;
    Outputs.LastPageVisible = true;
}
else
    Outputs.LastPageVisible = false;


const pages = [];

if(numPageSlots >= (numPages-2)) {
    // All pages fit
    for(let p = 1; p < numPages-1; p++) pages.push({
        id:Component.Object.id + '-' + p,
        Index:p
    })

    Outputs.LastEllipsis = false;
    Outputs.FirstEllipsis = false;
}
else {
    let firstIndex = selectedPage;
    let lastIndex = selectedPage;
    while(lastIndex - firstIndex < (numPageSlots-1)) {
        firstIndex = Math.max(1,firstIndex-1);
        lastIndex = Math.min(numPages-2,lastIndex+1);
    }

    let lastEllipsis = false;
    if(lastIndex < numPages-2) { 
        // We did not reach the end of num pages, make room for ...
        lastIndex--;
        lastEllipsis = true;
    }

    let firstEllipsis = false;
    if(firstIndex > 1) { 
        // We did not reach the end of num pages, make room for ...
        firstIndex++;
        firstEllipsis = true;
    }

    for(let p = firstIndex; p <= lastIndex; p++) pages.push({
        id:Component.Object.id + '-' + p,
        Index:p
    })

    Outputs.LastEllipsis = lastEllipsis;
    Outputs.FirstEllipsis = firstEllipsis;
}

Component.Object.NumPages = numPages;
Component.Object.Skip = Inputs.SelectedPage * Inputs.PageSize;

Outputs.BackEnabled = Inputs.SelectedPage > 0;
Outputs.NextEnabled = Inputs.SelectedPage < numPages-1;

Outputs.Pages = pages;

if(Component.Object['Selected Page'] > 0)
    Component.Object['Selected Page']--;

Outputs.Changed();

if(Component.Object['Selected Page'] < Component.Object.NumPages-1)
    Component.Object['Selected Page']++;

Outputs.Changed();

if(Inputs.Selected) {
    Outputs.BgColor = "Primary";
    Outputs.TextColor = "White";
}
else if(Inputs.Hover) {
    Outputs.BgColor = "Grey - 300";
    Outputs.TextColor = "Grey - 900";
}
else {
    Outputs.BgColor = "transparent";
    Outputs.TextColor = "Grey - 900";
}

// Make sure you can click through parent div
// so it doesn't block like normal popups
Inputs.This.getDOMElement().style.pointerEvents = 'none'
Inputs.This.getDOMElement().parentElement.style.pointerEvents = 'none'

Outputs.ToastType = [
    {
        Message:Inputs.Message,
        Type:Inputs.Type
    }
]

