let values = [];
let editIndex = null;

function updateHiddenInput() {
    $("[data-testid='hiddenInput']").val(JSON.stringify(values));
}

// 🔹 Render cards from array
function renderCards() {
    $(".output-cards").empty();

    values.forEach((obj, index) => {
        const card = $(`
            <div class="card" data-index="${index}">
                <button class="remove-btn">&times;</button>
                <div><strong>Value:</strong> ${obj.value}</div>
                <div><strong>Display Name:</strong> ${obj.displayName}</div>
            </div>
        `);

        // Remove
        card.find(".remove-btn").on("click", function (e) {
            e.stopPropagation();
            const idx = $(this).parent().data("index");
            values.splice(idx, 1);
            renderCards(); // re-render
        });

        // Edit
        card.on("click", function (e) {
            if ($(e.target).hasClass("remove-btn")) return; // skip if remove clicked
            const idx = $(this).data("index");
            editIndex = idx;
            $("[data-testid='valueInput']").val(values[idx].value);
            $("[data-testid='displayInput']").val(values[idx].displayName);
            $(".addBtns").text("Update");
        });

        $(".output-cards").append(card);
    });

    updateHiddenInput();
}

// 🔹 Add / Update button
$(".addBtns").on("click", function () {
    const value = $("[data-testid='valueInput']").val().trim();
    const displayName = $("[data-testid='displayInput']").val().trim();

    if (!value || !displayName) {
        alert("Please fill in both fields");
        return;
    }

    if (editIndex !== null) {
        // Update existing
        values[editIndex] = { value, displayName };
        editIndex = null;
        $(".addBtns").text("Add");
    } else {
        // Add new
        values.push({ value, displayName });
    }

    $("[data-testid='valueInput']").val("");
    $("[data-testid='displayInput']").val("");

    renderCards();
});

// 🔹 Example: Load array from API and render
if (typeof Inputs !== "undefined" && Array.isArray(Inputs.dataArr)) {
    values = [...Inputs.dataArr]; // copy to avoid mutation issues
    renderCards();
}

